  "private": true,
  "scripts": {
    "dev": "nuxi dev",
    "build": "nuxi build",
    "generate": "nuxi generate",
    "preview": "nuxi preview",
    "lint": "eslint ."
  },
  "devDependencies": {
    "@nuxt-themes/alpine": "1.6.5",
    "@nuxt/devtools": "^1.0.8",
    "@nuxt/eslint-config": "^0.2.0",
    "@nuxtjs/plausible": "^0.2.4",
    "@types/node": "^20.11.5",
    "eslint": "^8.56.0",
    "nuxt": "^3.9.3",
    "nuxt-gtag": "^1.1.2"
  },
  "pnpm": {
    "overrides": {
      "untyped": "1.4.0"
    }
  }
}

export default defineAppConfig({
  alpine: {
    /* Alpine configuration goes here */
  }
}

export default defineAppConfig({
  alpine: {
    title: 'Alpine',
    description: 'The minimalist blog theme',
    image: '/social-card-preview.png',
    // image can also be an object:
    image: {
      src: '/social-card-preview.png',
      alt: 'An image showcasing my project.',
      width: 400,
      height: 300
    }
  }
})

export default defineAppConfig({
  alpine: {
    socials: {
      twitter: 'nuxtlabs',
      instagram: 'wearenuxt',
      linkedin: {
        icon: 'uil:linkedin',
        label: 'LinkedIn',
        href: 'https://www.linkedin.com/company/nuxtlabs'
      },
      mastodon: {
        icon: 'simple-icons:mastodon',
        label: 'Mastodon',
        href: 'https://m.webtoo.ls/@nuxt',
        rel: 'me'
      }
    }
  }
})

defineAppConfig({
  alpine: {
    // Remove header with header: false
    header: {
      position: 'inline', // possible value are : 'none' | 'left' | 'center' | 'right' | 'inline'
      logo: false
    },
    // Remove header with footer: false
    footer: {
      credits: {
        enabled: true, // possible value are : true | false
        repository: 'https://www.github.com/nuxt-themes/alpine' // our github repository
      },
      navigation: false, // possible value are : true | false
      position: 'center', // possible value are : 'none' | 'left' | 'center' | 'right'
      message: 'Follow me on' // string that will be displayed on the footer (leave empty or delete to disable)
    }
    // Disable back to top button: false
    backToTop: {
      text: 'Back to top',
      icon: 'material-symbols:arrow-upward'
    }
  }
})

import { defineTheme } from 'pinceau'

export default defineTheme({
  alpine: {
    body: {
      // Update the background color in light & dark mode
      backgroundColor: {
        initial: '#f8fafc',
        dark: '#0f172a'
      }
    }
  }
})

@font-face {
  font-family: "NanumSquareRoundR";
  src: url("./fonts/NanumSquareRoundR.eot"); /* IE9 Compat Modes */
