# admin server
upstream admin {
    server 127.0.0.1:1448;
}
server {
    # Listen HTTP
    listen 80;
    server_name yourhost.com;

    # Proxy Config
    location / {
        proxy_pass http://admin;
        proxy_http_version 1.1;
        proxy_set_header X-Forwarded-Host $host;
        proxy_set_header X-Forwarded-Server $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header Host $http_host;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "Upgrade";
        proxy_pass_request_headers on;
    }
}

# admin server
upstream admin {
    server 127.0.0.1:1448;
}
server {
    # Listen HTTP
    server_name yourhost.com;

    # Proxy Config
    location / {
        proxy_pass http://admin;
        proxy_http_version 1.1;
        proxy_set_header X-Forwarded-Host $host;
        proxy_set_header X-Forwarded-Server $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header Host $http_host;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "Upgrade";
        proxy_pass_request_headers on;
    }

    listen 443 ssl; # managed by Certbot
    ssl_certificate /etc/letsencrypt/live/yourhost.com/fullchain.pem; # managed by Certbot
    ssl_certificate_key /etc/letsencrypt/live/yourhost.com/privkey.pem; # managed by Certbot
    include /etc/letsencrypt/options-ssl-nginx.conf; # managed by Certbot
    ssl_dhparam /etc/letsencrypt/ssl-dhparams.pem; # managed by Certbot

}


server {
    if ($host = yourhost.com) {
        return 301 https://$host$request_uri;
    } # managed by Certbot


    listen 80;
    server_name yourhost.com;
    return 404; # managed by Certbot
}

server {
        listen 443;
        listen [::]:443;

        root /var/www/html;

        index index.html index.htm index.nginx-debian.html;

        server_name techy100.com www.techy100.com;

        ssl on;
        ssl_certificate /etc/nginx/ssl/techy100.com.crt;
        ssl_certificate_key /etc/nginx/ssl/techy100.com.csr;

        location / {
                # First attempt to serve request as file, then
                # as directory, then fall back to displaying a 404.
                try_files $uri $uri/ =404;
        }

        # deny access to .htaccess files, if Apache's document root
        # concurs with nginx's one

        location ~ /\.ht {
               deny all;
        }

}

# redirecting from HTTP to HTTPS
#
server {
        listen 80;
        listen [::]:80;

        root /var/www/html;

        index index.html index.htm index.nginx-debian.html;

        server_name techy100.com www.techy100.com;

        return 301 https://$host$request_uri; # Redirect
}

server {
        listen 443 ssl http2;
        listen [::]:443 ssl http2;

        server_name yourhost.com www.yourhost.com;

        root /var/www/html;

        index index.html index.htm index.nginx-debian.html;


        ssl on;
        ssl_certificate /etc/nginx/ssl/dodevops.crt;
        ssl_certificate_key /etc/nginx/ssl/dodevops.csr;

        # deny access to .htaccess files, if Apache's document root
        # concurs with nginx's one
        location ~ /\.ht {
               deny all;
        }

        location / {
                # First attempt to serve request as file, then
                # as directory, then fall back to displaying a 404.
               try_files $uri $uri/ =404;
        }


        # directus conf : start
        location /main {
                rewrite ^/main/(.*)$ /$1 break;
                proxy_pass http://127.0.0.1:8055;
                proxy_set_header Host $host;
                proxy_set_header X-Real-IP $remote_addr;
                proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
                proxy_set_header REMOTE-HOST $remote_addr;
                add_header X-Cache $upstream_cache_status;

                proxy_connect_timeout 30s;
                proxy_read_timeout 86400s;
                proxy_send_timeout 30s;
                proxy_http_version 1.1;
                proxy_set_header Upgrade $http_upgrade;
                proxy_set_header Connection "upgrade";
        }
        # directus conf : end

        client_max_body_size 50m;
        access_log  /var/log/nginx/directus.log;
        error_log  /var/log/nginx/directus_error.log;
}

